package com.chestnutcake.leetcode

import org.scalatest.flatspec.AnyFlatSpec
import org.scalatest.matchers.should.Matchers

import CharacterReplacement.characterReplacement

class CharacterReplacementSpec extends AnyFlatSpec with Matchers {
  "\"ABAB\" with k 2" should "return 4" in {
    val s = "ABAB"
    characterReplacement(s, 2) shouldEqual 4
  }

  "\"AABABBA\" with k 1" should "return 4" in {
    val s = "AABABBA"
    characterReplacement(s, 1) shouldEqual 4
  }

  "\"AAAA\" with k 2" should "return 4" in {
    val s = "AAAA"
    characterReplacement(s, 2) shouldEqual 4
  }

  "\"ABAA\" with k 0" should "return 2" in {
    val s = "ABAA"
    characterReplacement(s, 0) shouldEqual 2
  }

  "\"AAAABBB\" with k 0" should "return 4" in {
    val s = "AAAABBB"
    characterReplacement(s, 0) shouldEqual 4
  }

  "\"AAAA\" with k 0" should "return 4" in {
    val s = "AAAA"
    characterReplacement(s, 0) shouldEqual 4
  }

  "\"KRSCDCSONAJNHLBMDQGIFCPEKPOHQIHLTDIQGEKLRLCQNBOHNDQGHJPNDQPERNFSSSRDEQLFPCCCARFMDLHADJADAGNNSBNCJQOF\" with k 4" should "return 8" in {
    val s = "KRSCDCSONAJNHLBMDQGIFCPEKPOHQIHLTDIQGEKLRLCQNBOHNDQGHJPNDQPERNFSSSRDEQLFPCCCARFMDLHADJADAGNNSBNCJQOF"
    characterReplacement(s, 4) shouldEqual 7
  }

  "\"\" with k 100" should "return 115" in {
    val s = ""
    characterReplacement(s, 100) shouldEqual 115
  }
}
